package practise;

public class lecloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for(int apple=1;apple<=100;apple++) {
		System.out.println();	
			}
		
		
		for(int jack=0;jack<10;jack+=2){
		System.out.println(jack);	
		}
	
	    for(int dog=0;dog<10;dog+=2) {
	    System.out.println(dog);	
	    	
	   } 
	    
	    
	    
	    
	    for(int a=51;a<100;a+=2) {
	    System.out.println();	
	    }
	    
	    
	    for(int b=10;b>1;b--) {
	    System.out.println();	
	    }
	    
	    
	    
     for(int c=200;c>=50;c-=1) {
     System.out.println(c); 
     }
	    
	    
	    
	    
	
	
	
	
	
	
	}
	

}
